mapper.update:
UPDATE ${tableName}
SET APP_NAME = #{appInfoDO.appName},
    APP_INFO = #{appInfoDO.appInfoDO},
    API_LIST = #{appInfoDO.apiList}
WHERE APP_ID = #{appInfoDO.appId}

UPDATE ${tableName}
SET GROUP_NUMBER = GROUP_NUMBER + #{count}
WHERE APP_ID = #{appId}

UPDATE ${tableName}
SET GROUP_NUMBER = GROUP_NUMBER - #{count}
WHERE APP_ID = #{appId}
