mapper.update:
UPDATE `group_info`
SET user_number = user_number + #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}

UPDATE `group_info`
SET face_number = face_number + #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}

UPDATE `group_info`
SET user_number = user_number - #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}

UPDATE `group_info`
SET face_number = face_number - #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}

mapper.select:
SELECT id, group_id, face_number FROM group_info WHERE app_id = #{appId} AND group_id IN
#{item}
AND is_delete = 0;
