mapper.update:
UPDATE `user_info`
SET face_number = face_number + #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}
  AND user_id = #{userId}

UPDATE `user_info`
SET face_number = face_number - #{count}
WHERE app_id = #{appId}
  AND group_id = #{groupId}
  AND user_id = #{userId}

mapper.select:
SELECT
DISTINCT
a.group_id
FROM
user_info a


        a.app_id = #{appId}


        AND  a.user_id = #{userId}


        AND  a.group_id = #{groupId}

SELECT * FROM user_info a WHERE a.app_id = #{appId} AND a.gid in
#{item}
AND a.id in
#{item}

SELECT * FROM `user_info` a
WHERE a.app_id = #{appId}
AND a.group_id in

    #{item}

AND a.user_id = #{userId}
